import axios from 'axios';
import React, { Component } from 'react';
import Marquee from 'react-marquee-master';
import '../styles/Lists.scss';

export default class Lists extends Component {

    state = {
        topPlayers: false
    }

    componentDidMount() {
        axios.get('http://criminalimpulse.com/api/topplayerlist')
            .then(res => {
                const topPlayers = res.data;
                this.setState({ topPlayers });
            })
    }

    render() {

        console.log(this.state.topPlayers);

        if (this.state.topPlayers) {

            console.log(this.state.topPlayers.length);

            return [
                <div height='300px' className='player-left'>
                    <p className='listHeading'>Menu Heading</p>
                    <Marquee minHeight='250' marqueeItems={this.state.topPlayers} />
                </div>,
                <div height='300px' className='player-right'>
                    <p className='listHeading'>Menu Heading</p>
                    <Marquee minHeight='250' marqueeItems={this.state.topPlayers} />
                </div>
            ];
        }

        return false;
    }
}
