import React, { Component, useRef } from "react";

import "../styles/Pop.scss";

export default class Pop extends Component {

    constructor(props) {
        super(props);

        this.ref = React.createRef();
    }


    render() {


        const events = {
            "event": [
                {
                    "event": "Robert attacked Schwartz and won the fight",
                },
                {
                    "event": "Rocky attacked Rockwood and Lost the fight",
                },
                {
                    "event": "Hackers team hacked all PC of police department",
                },
                {
                    "event": "Robert attacked Schwartz and tie the fight",
                },
                {
                    "event": "Robert attacked Schwartz and sattlement the fight",
                }
            ]
        }



        const ref = this.ref;

        const listed = events.event.map(function (value, key) {

            let style;

            if (ref.current) {
                const current = ref.current;

                style = {
                    position: 'absolute',
                }

                if (current.offsetLeft) style.left = current.offsetLeft;
                if (current.offsetRight) style.right = current.offsetRight;
                if (current.offsetTop) style.right = current.offsetTop;
                if (current.offsetBottom) style.right = current.offsetBottom;

                console.log('Hit');

            } else {

                style = {
                    position: 'absolute',
                    top: '50%',
                    right: Math.floor(Math.random() * 30) + '%',
                    // bottom: Math.floor(Math.random() * 30) + '%'
                }
            }

            return <p ref={ref} style={style}>{value.event}</p>;
        });

        return [
            <div className="action">
                {listed}
            </div>
        ];
    }
}
