const images = [
    "main1.jpg", "main2.jpg", "main3.jpg"
];

var k;
const start = images[0];
const last = images.length - 1;
let currentPosition = 0;

const changeImage = (image) => {
    const body = document.querySelector('.app');
    body.style.backgroundImage = "url('" + image + "')";


}

const setCurrentPosition = () => {
    currentPosition = (currentPosition < images.length) ? ++currentPosition : images.length;
    console.log(currentPosition);
    changeImage(images[currentPosition]);

    if (currentPosition === images.length) {
        currentPosition = 0;
        clearInterval(k);
        k = setInterval(setCurrentPosition, 1000);
    }

}

k = setInterval(setCurrentPosition, 1000);

console.log(images[currentPosition]);
