import axios from 'axios';
import React, { Component } from 'react';
import Marquee from 'react-marquee-master';
import '../styles/Lists.scss';

export default class Lists extends Component {

    state = {
        loading: false, players: [],
    }

    componentDidMount() {
        if (this.state.loading){
            axios.get('http://criminalimpulse.com/api/topplayerlist')
                .then(res => {
                    this.setState({ loading: false, loaded: true, players: res.data });
                })

        }
    }

    render() {
        console.log(this.state);
        if (this.state.loaded) {

            return (
                <div height='300px' className='player-left'>
                    <p className='listHeading'>Menu Heading</p>
                    <Marquee minHeight='250' marqueeItems={this.state.players} />
                </div>,
                <div height='300px' className='player-right'>
                    <p className='listHeading'>Menu Heading</p>
                    <Marquee minHeight='250' marqueeItems={this.state.players} />
                </div>
            )
        }

        return false;
    }
}
