import axios from 'axios';
import React, { Component, useEffect, useState } from 'react';
import Marquee from 'react-marquee-master';
import '../styles/Lists.scss';

export default function Example() {
    const [status, setStatus] = useState('idle');
    const [hasError, setErrors] = useState(false);
    let [responseData = {}, setResponseData] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            setStatus('fetching');
            await axios.get('http://criminalimpulse.com/api/topplayerlist')
                .then((response) => {
                    setResponseData(JSON.stringify(response.data));
                    setStatus('fetched');
                })
                .catch(error => {
                    setErrors(error);
                    console.log(error);
                });
        }
        fetchData();

    }, []);

    const listHere = responseData;
    return (<>
        <div height='300px' className='player-left'>
            <p className='listHeading'>Menu Heading</p>

            <Marquee minHeight='250' marqueeItems={responseData} />
        </div>,
        <div height='300px' className='player-right'>
            <p className='listHeading'>Menu Heading</p>
            <Marquee minHeight='250' marqueeItems={responseData} />
        </div>
    </>)

}
