import React, { Component } from 'react';

class PopupGenerator extends Component {
    constructor() {
        super();
        this.data = [1, 2, 3, 4, 5, 6, 7, 8, 9, 6, 4, 3, 2, 1, 5];
        this.state = {
            count: 5,
            defaultCount: 5,
        };
    }
    handleCount() {
        let count = this.state.defaultCount;
        count = count + this.state.count;
        this.setState({ count });
    }
    showDate(){
        const count = this.state.count;
        return showData = (item, index) => {
            return ((index < count) ? item : '');
        };

    }
}

export default PopupGenerator;
